import Bg from "@/components/bg";
import Container from "@/components/container";
import {
  SectionHeader,
  SectionHeaderSummary,
  SectionHeaderTitle,
} from "@/components/home/section-header";
import cx from "@/utils/cx";

export default function HomeHeroCustomer() {
  return (
    <section className="relative z-10 py-8 md:py-16">
      <Bg />

      <Container>
        {/* title */}
        <SectionHeader>
          <SectionHeaderTitle>Meet our Customers</SectionHeaderTitle>
          <SectionHeaderSummary>
            Upstash enables companies of all sizes to create at the moment of
            inspiration
          </SectionHeaderSummary>
        </SectionHeader>

        {/* QUOTES */}
        <div
          className={cx(
            "mt-16 grid grid-cols-3 gap-2 rounded-2xl md:mt-24 md:grid-cols-6 md:gap-6",
          )}
        >
          {customers
            .filter((c) => c.quote)
            .map(({ name, icon, quote }) => (
              <div
                key={name}
                className={cx(
                  "group flex flex-col overflow-hidden px-4 py-3 sm:py-4",
                  "rounded-2xl bg-bg-mute md:rounded-4xl dark:bg-bg-mute dark:hover:bg-white",
                  "col-span-3 bg-white py-8 shadow-sm sm:px-16 sm:py-10",
                )}
              >
                <span className="flex h-10 items-center justify-center opacity-80 transition group-hover:text-primary group-hover:opacity-100">
                  {icon}
                </span>
                <p
                  className={cx(
                    "mt-4 grow md:text-xl",
                    "text-balance text-text-mute dark:text-text",
                    "dark:group-hover:text-bg",
                  )}
                >
                  “{quote}”
                </p>
              </div>
            ))}
        </div>

        {/* CARDS */}
        <div
          className={cx(
            "mt-16 flex flex-wrap justify-center gap-2 rounded-2xl md:mt-24 md:gap-6",
          )}
        >
          {customers
            .filter((c) => !c.quote)
            .map(({ name, icon, quote }) => (
              <div
                key={name}
                className={cx(
                  "group flex w-[180px] flex-col overflow-hidden px-4 py-3 sm:py-4",
                  "rounded-2xl bg-bg-mute md:rounded-4xl dark:bg-bg-mute dark:hover:bg-white",
                )}
              >
                <span className="flex h-10 items-center justify-center opacity-80 transition group-hover:text-primary group-hover:opacity-100">
                  {icon}
                </span>
              </div>
            ))}
        </div>
      </Container>
    </section>
  );
}

